import React, { useState, useEffect } from 'react';
import UserService from '../service/UserService';
import { Link } from 'react-router-dom';
import './ProfilePage.css';  // Import custom CSS for styling

function ProfilePage() {
    const [profileInfo, setProfileInfo] = useState({});

    useEffect(() => {
        fetchProfileInfo();
    }, []);

    const fetchProfileInfo = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await UserService.getYourProfile(token);
            setProfileInfo(response.ourUsers);
        } catch (error) {
            console.error('Error fetching profile information:', error);
        }
    };

    return (
        <div className="profile-page-container">
            <div className="profile-card">
                <h2>Profile Information</h2>
                <p><strong>Name:</strong> {profileInfo.name}</p>
                <p><strong>Email:</strong> {profileInfo.email}</p>
                <p><strong>City:</strong> {profileInfo.city}</p>
                {profileInfo.role === "ADMIN" && (
                    <button className="update-btn">
                        <Link to={`/update-user/${profileInfo.id}`}>Update This Profile</Link>
                    </button>
                )}
            </div>
        </div>
    );
}

export default ProfilePage;
