import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TodoList.css'

function TodoList() {
  // State variables to store todos, new todo, username, and password
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Function to handle creating a new todo
  const handleCreateTodo = () => {
    // Create a new todo object
    const todo = {
      title: newTodo,
      description: '',
      completed: false,
    };

    // Send a POST request to create the new todo
    axios.post('http://localhost:8080/api/todos', todo)
      .then((response) => {
        // Add the new todo to the list of todos
        setTodos([...todos, response.data]);
        // Clear the new todo input field
        setNewTodo('');
      })
      .catch((error) => {
        console.error(error);
      });
  };

  // Function to handle updating a todo
  const handleUpdateTodo = (id) => {
    // Find the todo to update
    const todoToUpdate = todos.find((todo) => todo.id === id);

    // Update the todo
    const updatedTodo = {
      ...todoToUpdate,
      title: 'Updated Todo',
      description: '',
      completed: true,
    };

    // Send a PUT request to update the todo
    axios.put(`http://localhost:8080/api/todos/${id}`, updatedTodo)
      .then((response) => {
        // Update the list of todos
        setTodos(todos.map((todo) => (todo.id === id ? response.data : todo)));
      })
      .catch((error) => {
        console.error(error);
      });
  };

  // Function to handle deleting a todo
  const handleDeleteTodo = (id) => {
    // Send a DELETE request to delete the todo
    axios.delete(`http://localhost:8080/api/todos/${id}`)
      .then(() => {
        // Update the list of todos
        setTodos(todos.filter((todo) => todo.id !== id));
      })
      .catch((error) => {
        console.error(error);
      });
  };

  // Function to handle logging in
  const handleLogin = () => {
    // Create a login object
    const login = {
      username,
      password,
    };

    // Send a POST request to log in
    axios.post('http://localhost:8080/login', login)
      .then((response) => {
        console.log(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  // Use effect to fetch todos when the component mounts
  useEffect(() => {
    axios.get('http://localhost:8080/api/todos')
      .then((response) => {
        setTodos(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <div className="todo-list">
      <h1>Todo List</h1>
      <ul className="todo-list-items">
        {todos.map((todo) => (
          <li key={todo.id} className="todo-list-item">
            <span className="todo-title">{todo.title}</span>
            <button className="todo-button" onClick={() => handleUpdateTodo(todo.id)}>Update</button>
            <button className="todo-button" onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
          </li>
        ))}
      </ul>
      <input
        type="text"
        value={newTodo}
        onChange={(e) => setNewTodo(e.target.value)}
        placeholder="New Todo"
        className="new-todo-input"
      />
      <button className="create-todo-button" onClick={handleCreateTodo}>Create Todo</button>
      <div className="login-form">
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          className="login-input"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          className="login-input"
        />
        <button className="login-button" onClick={handleLogin}>Login</button>
      </div>
    </div>
  );
}

export default TodoList;
